import { useEffect, useState } from "react";
import "./AdmissionButton.css";
import AdmissionForm from "../../src/assets/pdfs/Admission_Form.pdf";

const AdmissionStarburst = ({ text = "ADMISSION OPEN" }) => {
  const [size, setSize] = useState(getResponsiveSize());

  function getResponsiveSize() {
    if (window.innerWidth <= 480) return 40;  // Mobile
    if (window.innerWidth <= 768) return 90;  // Tablet
    return 120; // Desktop
  }

  useEffect(() => {
    const handleResize = () => setSize(getResponsiveSize());
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const r = size / 2;
  const fontSize = size * 0.14;
  const labelHeight = size * 0.3;
  const labelY = -(labelHeight / 2);

  return (
    <div className="starburst-wrap">
      <a
        href={AdmissionForm}
        target="_blank"
        rel="noreferrer"
        className="starburst-link"
        aria-label={text}
        title={text}
      >
        <svg
          className="starburst-svg"
          width={size}
          height={size}
          viewBox={`0 0 ${size} ${size}`}
          role="img"
          aria-labelledby="starburstTitle"
        >
          <title id="starburstTitle">{text}</title>

          <g transform={`translate(${r} ${r}) rotate(-10)`}>
            {(() => {
              const spikes = 32;
              const outer = r * 0.98;
              const inner = r * 0.7;
              const pts = [];
              for (let i = 0; i < spikes * 2; i++) {
                const angle = (i * Math.PI) / spikes;
                const rad = i % 2 === 0 ? outer : inner;
                pts.push(`${Math.cos(angle) * rad},${Math.sin(angle) * rad}`);
              }
              const d = `M ${pts.join(" L ")} Z`;
              return <path className="star" d={d} />;
            })()}

            <rect
              x={-r * 0.72}
              y={labelY}
              width={r * 1.44}
              height={labelHeight}
              rx={labelHeight * 0.25}
              className="label"
            />

            <text
              className="label-text"
              x="0"
              y={fontSize * 0.35}
              textAnchor="middle"
              focolor="#f70909ff"
              style={{ fontSize: `${fontSize}px` }}
            >
              {text.split(" ").map((word, i) => (
                <tspan key={i} x="0" dy={i === 0 ? 0 : fontSize}>
                  {word}
                </tspan>
              ))}
            </text>
          </g>
        </svg>
      </a>
    </div>
  );
};

export default AdmissionStarburst;
