import React, { useState, useEffect } from "react";
import "./GalleryCarousel.css";

// Auto-import all images from a folder
function importAll(r) {
  return r.keys().map(r);
}
const images = importAll(
  require.context("../assets/images/photo galary", false, /\.(png|jpe?g|svg)$/)
);

export default function Gallery() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [lightboxOpen, setLightboxOpen] = useState(false);

  const imagesPerPage = 4;
  const totalPages = Math.ceil(images.length / imagesPerPage);

  useEffect(() => {
    if (!lightboxOpen) {
      const timer = setInterval(() => {
        nextImage();
      }, 3000);
      return () => clearInterval(timer);
    }
  }, [lightboxOpen, currentIndex]);

  const nextImage = () => {
    setCurrentIndex((prev) => (prev + 1) % images.length);
  };

  const prevImage = () => {
    setCurrentIndex((prev) => (prev - 1 + images.length) % images.length);
  };

  const openLightbox = (index) => {
    setCurrentIndex(index);
    setLightboxOpen(true);
  };

  const closeLightbox = () => setLightboxOpen(false);

  // Current images to show
  const currentImages = [];
  for (let i = 0; i < imagesPerPage; i++) {
    currentImages.push(images[(currentIndex + i) % images.length]);
  }

  // Pagination page index
  const currentPage = Math.floor(currentIndex / imagesPerPage);

  return (
    <div className="gallery-wrapper">
      <div className="gallery-container">
        <button className="nav-btn left" onClick={prevImage}>
          &#10094;
        </button>

        <div className="gallery-grid">
          {currentImages.map((img, index) => (
            <div className="gallery-card" key={index}>
              <img
                src={img}
                alt={`Gallery ${index}`}
                className="gallery-thumb"
                onClick={() =>
                  openLightbox((currentIndex + index) % images.length)
                }
              />
            </div>
          ))}
        </div>

        <button className="nav-btn right" onClick={nextImage}>
          &#10095;
        </button>
      </div>

      {/* Pagination Dots */}
      <div className="pagination-dots">
        {Array.from({ length: totalPages }).map((_, index) => (
          <span
            key={index}
            className={`dot ${index === currentPage ? "active" : ""}`}
            onClick={() => setCurrentIndex(index * imagesPerPage)}
          ></span>
        ))}
      </div>

      {/* Lightbox */}
      {lightboxOpen && (
        <div className="lightbox">
          <span className="close" onClick={closeLightbox}>
            &times;
          </span>
          <button className="prev" onClick={prevImage}>
            &#10094;
          </button>
          <img
            src={images[currentIndex]}
            alt="Full View"
            className="lightbox-img"
          />
          <button className="next" onClick={nextImage}>
            &#10095;
          </button>
        </div>
      )}
    </div>
  );
}
