import React from 'react';


import './App.css';
import './components/Responsive.css';

import AppRouter from './AppRouter';



function App() {
  React.useEffect(() => {
    // Load Google Translate script only once
    if (!window.google || !window.google.translate) {
      const script = document.createElement('script');
      script.src = 'https://translate.google.com/translate_a/element.js?cb=googleTranslateElementInit';
      script.async = true;
      document.body.appendChild(script);
      window.googleTranslateElementInit = function() {
        new window.google.translate.TranslateElement({
          pageLanguage: 'en',
          includedLanguages: 'en,hi',
          autoDisplay: false
        }, 'hidden_google_translate_element');
      };
    }
  }, []);

  // Custom language switcher
  const handleLanguageChange = (e) => {
    const lang = e.target.value;
    const frame = document.querySelector('iframe.goog-te-menu-frame');
    if (frame) {
      const innerDoc = frame.contentDocument || frame.contentWindow.document;
      const langLinks = innerDoc.querySelectorAll('.goog-te-menu2-item span.text');
      langLinks.forEach(function(node) {
        if (node.innerText.toLowerCase().indexOf(lang === 'en' ? 'english' : 'hindi') > -1) {
          node.click();
        }
      });
    }
  };

  return (
    <>
      <div style={{width:'100vw',background:'#f5f5f5',padding:'0.5rem 0',textAlign:'right',position:'fixed',top:0,left:0,zIndex:2000}}>
        <span style={{fontWeight:600,marginRight:8}}>Language:</span>
        <select onChange={handleLanguageChange} style={{fontWeight:600,padding:'0.2rem 0.7rem',borderRadius:4,border:'1px solid #1976d2'}} defaultValue="en">
          <option value="en">English</option>
          <option value="hi">हिन्दी</option>
        </select>
        <div id="hidden_google_translate_element" style={{display:'none'}}></div>
      </div>
      <div style={{paddingTop: '2.5rem'}}>
        <AppRouter />
      </div>
    </>
  );
}

export default App;
