import React from "react";
import "./AdmissionButton.css";
import AdmissionForm from "../../src/assets/pdfs/Admission_Form.pdf";

const AdmissionStarburst = ({ size = 120, text = "ADMISSION OPEN" }) => {
  const s = size;          // overall size (p
  // x)
  const r = s / 2;         // radius for viewbox convenience

  return (
    <div className="starburst-wrap">
      <a href={AdmissionForm} target = "_blank" rel="noreferrer" className="starburst-link" aria-label={text} title={text}>
        {/* SVG starburst (32 points) */}
        <svg
          className="starburst-svg"
          width={s}
          height={s}
          viewBox={`0 0 ${s} ${s}`}
          role="img"
          aria-labelledby="starburstTitle"
        >
          <title id="starburstTitle">{text}</title>

          {/* star shape */}
          <g transform={`translate(${r} ${r}) rotate(-10)`}>
            {/* build spikes by alternating inner/outer radii */}
            {(() => {
              const spikes = 32;
              const outer = r * 0.98;
              const inner = r * 0.70;
              const pts = [];
              for (let i = 0; i < spikes * 2; i++) {
                const angle = (i * Math.PI) / spikes;
                const rad = i % 2 === 0 ? outer : inner;
                pts.push(`${Math.cos(angle) * rad},${Math.sin(angle) * rad}`);
              }
              const d = `M ${pts.join(" L ")} Z`;
              return (
                <path className="star" d={d} />
              );
            })()}
            {/* center label plate so text is always readable */}
            <rect
              x={-r * 0.72}
              y={-22}
              width={r * 1.44}
              height={44}
              rx={10}
              className="label"
            />
            <text
              className="label-text"
              x="0"
              y="8"
              textAnchor="middle"
              fontWeight="200"
            >
              {text}
            </text>
          </g>
        </svg>
      </a>
    </div>
  );
};

export default AdmissionStarburst;
