import React, { useState } from 'react';
import { Link } from 'react-router-dom';
// import { useTranslation } from 'react-i18next';
import './Navbar.css';

function Navbar() {
  const [openDropdown, setOpenDropdown] = useState(null);
  const handleDropdown = (name) => setOpenDropdown(name);
  const closeDropdown = () => setOpenDropdown(null);
  return (
    <nav className="navbar" style={{display:'flex',alignItems:'center',justifyContent:'space-between',background:'#1976d2',color:'#fff',padding:'0.5rem 2rem',gap: '10px',boxShadow:'0 2px 8px rgba(0,0,0,0.07)'}}>
      <div className="navbar-logo" style={{fontWeight:700,fontSize:'1.3rem'}}>
        <Link to="/" style={{color:'#fff',textDecoration:'none'}}>Food Craft Institute Sumerpur</Link>
      </div>
      <ul className="navbar-links" style={{display:'flex',gap:'1.5rem',listStyle:'none',margin:0,padding:0}}>
        <li><Link to="/" style={{color:'#fff',textDecoration:'none',fontWeight:500}}>Home</Link></li>
        <li className="dropdown" onMouseEnter={()=>handleDropdown('about')} onMouseLeave={closeDropdown} style={{position:'relative'}}>
          <span style={{cursor:'pointer'}}>About Us ▾</span>
          {openDropdown==='about' && (
            <ul className="dropdown-menu" style={{position:'absolute',top:'2rem',left:0,background:'#fff',color:'#222',boxShadow:'0 2px 8px rgba(0,0,0,0.12)',borderRadius:6,minWidth:180,padding:'0.5rem 0',zIndex:100}}>
              <li><Link to="/about" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>About Us</Link></li>
              <li><Link to="/mission-vision" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>Principal Message</Link></li>
              <li><Link to="/faculty" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>Why Us</Link></li>
              <li><Link to="/infrastructure" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>Overview & History</Link></li>
              <li><Link to="/placement" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>Vision & Objective</Link></li>
              <li><Link to="/testimonials" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>Our Staff</Link></li>
            </ul>
          )}
        </li>
        <li className="dropdown" onMouseEnter={()=>handleDropdown('student')} onMouseLeave={closeDropdown} style={{position:'relative'}}>
          <span style={{cursor:'pointer'}}>Student Section ▾</span>
          {openDropdown==='student' && (
            <ul className="dropdown-menu" style={{position:'absolute',top:'2rem',left:0,background:'#fff',color:'#222',boxShadow:'0 2px 8px rgba(0,0,0,0.12)',borderRadius:6,minWidth:180,padding:'0.5rem 0',zIndex:100}}>
              <li><Link to="/syllabus" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>Syllabus</Link></li>
              <li><Link to="/fee-structure" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>Fee Structure</Link></li>
              <li><Link to="/general-rules" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>General Rules</Link></li>
              <li><Link to="/anti-ragging" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>Anti Ragging Circular</Link></li>
            </ul>
          )}
        </li>
        <li className="dropdown" onMouseEnter={()=>handleDropdown('admissions')} onMouseLeave={closeDropdown} style={{position:'relative'}}>
          <span style={{cursor:'pointer'}}>Admissions ▾</span>
          {openDropdown==='admissions' && (
            <ul className="dropdown-menu" style={{position:'absolute',top:'2rem',left:0,background:'#fff',color:'#222',boxShadow:'0 2px 8px rgba(0,0,0,0.12)',borderRadius:6,minWidth:180,padding:'0.5rem 0',zIndex:100}}>
              <li><Link to="/fee-structure" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>Fee Structure</Link></li>
              <li><Link to="/admission-form" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>Admission Form</Link></li>
              <li><Link to="/medical-fitness-certificate" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>Medical Fitness Certificate</Link></li>
            </ul>
          )}
        </li>
        <li className="dropdown" onMouseEnter={()=>handleDropdown('courses')} onMouseLeave={closeDropdown} style={{position:'relative'}}>
          <span style={{cursor:'pointer'}}>Courses ▾</span>
          {openDropdown==='courses' && (
            <ul className="dropdown-menu" style={{position:'absolute',top:'2rem',left:0,background:'#fff',color:'#222',boxShadow:'0 2px 8px rgba(0,0,0,0.12)',borderRadius:6,minWidth:180,padding:'0.5rem 0',zIndex:100}}>
              <li><Link to="/courses" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>All Courses</Link></li>
              <li><Link to="/diploma/FoodProduction" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>Diploma in Food Production</Link></li>
              <li><Link to="/diploma/FoodBeverageService" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>Diploma in Food and Beverage Service</Link></li>
              <li><Link to="/diploma/FrontOffice" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>Diploma in Front Office</Link></li>
              <li><Link to="/courses/skill-development" style={{color:'#222',textDecoration:'none',padding:'0.5rem 1rem',display:'block'}}>Skill Development</Link></li>
            </ul>
          )}
        </li>
        <li><Link to="/contact" style={{color:'#fff',textDecoration:'none',fontWeight:500}}>Contact</Link></li>
      </ul>
    </nav>
  );
}

export default Navbar;
