import React, { useState, useEffect } from "react";
import "./GalleryCarousel.css";

// Auto-import all images from folder (src/assets/gallery)
function importAll(r) {
  return r.keys().map(r);
}
const images = importAll(require.context("../assets/images/photo galary", false, /\.(png|jpe?g|svg)$/));

export default function Gallery() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [lightboxOpen, setLightboxOpen] = useState(false);

  // Auto slideshow
  useEffect(() => {
    if (!lightboxOpen) {
      const timer = setInterval(() => {
        setCurrentIndex((prev) => (prev + 1) % images.length);
      }, 3000);
      return () => clearInterval(timer);
    }
  }, [lightboxOpen]);

  // Open lightbox
  const openLightbox = (index) => {
    setCurrentIndex(index);
    setLightboxOpen(true);
  };

  // Close lightbox
  const closeLightbox = () => setLightboxOpen(false);

  // Next/Prev
  const showNext = () => setCurrentIndex((prev) => (prev + 1) % images.length);
  const showPrev = () => setCurrentIndex((prev) => (prev - 1 + images.length) % images.length);

  return (
    <div className="gallery-container">
      {/* Thumbnail grid */}
      <div className="gallery-grid">
        {images.map((img, index) => (
          <img
            key={index}
            src={img}
            alt={`Gallery ${index}`}
            className="gallery-thumb"
            onClick={() => openLightbox(index)}
          />
        ))}
      </div>

      {/* Lightbox */}
      {lightboxOpen && (
        <div className="lightbox">
          <span className="close" onClick={closeLightbox}>
            &times;
          </span>
          <button className="prev" onClick={showPrev}>
            &#10094;
          </button>
          <img src={images[currentIndex]} alt="Full View" className="lightbox-img" />
          <button className="next" onClick={showNext}>
            &#10095;
          </button>
        </div>
      )}
    </div>
  );
}
