import React from "react";
import "./AdmissionButton.css";
import AdmissionForm from "../../src/assets/pdfs/Admission_Form.pdf";

const AdmissionStarburst = ({ size = 120, text = "ADMISSION OPEN" }) => {
  const r = size / 2;

  // Dynamic scaling factors
  const fontSize = size * 0.14; // 14% of total size
  const labelHeight = size * 0.3; // 30% of total size
  const labelY = -(labelHeight / 2); // center vertically

  return (
    <div className="starburst-wrap">
      <a
        href={AdmissionForm}
        target="_blank"
        rel="noreferrer"
        className="starburst-link"
        aria-label={text}
        title={text}
      >
        <svg
          className="starburst-svg"
          width={size}
          height={size}
          viewBox={`0 0 ${size} ${size}`}
          role="img"
          aria-labelledby="starburstTitle"
        >
          <title id="starburstTitle">{text}</title>

          <g transform={`translate(${r} ${r}) rotate(-10)`}>
            {(() => {
              const spikes = 32;
              const outer = r * 0.98;
              const inner = r * 0.70;
              const pts = [];
              for (let i = 0; i < spikes * 2; i++) {
                const angle = (i * Math.PI) / spikes;
                const rad = i % 2 === 0 ? outer : inner;
                pts.push(`${Math.cos(angle) * rad},${Math.sin(angle) * rad}`);
              }
              const d = `M ${pts.join(" L ")} Z`;
              return <path className="star" d={d} />;
            })()}

            {/* Scaled label */}
            <rect
              x={-r * 0.72}
              y={labelY}
              width={r * 1.44}
              height={labelHeight}
              rx={labelHeight * 0.25}
              className="label"
            />

            {/* Scaled text */}
            <text
              className="label-text"
              x="0"
              y={fontSize * 0.35}
              textAnchor="middle"
              fontWeight="800"
              style={{ fontSize: `${fontSize}px` }}
            >
              {text.split(" ").map((word, i) => (
                <tspan key={i} x="0" dy={i === 0 ? 0 : fontSize}>
                  {word}
                </tspan>
              ))}
            </text>
          </g>
        </svg>
      </a>
    </div>
  );
};

export default AdmissionStarburst;
