import React, { useState } from 'react';
import emailjs from 'emailjs-com';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';

import './Contact.css';


function Contact() {
  const navigate = useNavigate();
  const { t } = useTranslation();

  const [form, setForm] = useState({ name: '', email: '', message: '' });
  const [errors, setErrors] = useState({});

  const validate = () => {
    const errs = {};
    if (!form.name.trim()) errs.name = t('name_required');
    if (!form.email.trim()) {
      errs.email = t('email_required');
    } else if (!/^[^@\s]+@[^@\s]+\.[^@\s]+$/.test(form.email)) {
      errs.email = t('email_invalid');
    }
    if (!form.message.trim()) errs.message = t('message_required');
    return errs;
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: '' });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const errs = validate();
    if (Object.keys(errs).length > 0) {
      setErrors(errs);
      return;
    }
    emailjs.send(
      'service_eh464j4', // Replace with your EmailJS Service ID
      'template_xxsmrk8', // Replace with your EmailJS Template ID
      {
        from_name: form.name,
        from_email: form.email,
        message: form.message,
      },
      'B2sB-ke3Bife25Xeo' // Replace with your EmailJS Public Key
    )
    .then((result) => {
      window.alert(t('contact_success'));
      setForm({ name: '', email: '', message: '' });
      navigate('/');
    }, (error) => {
      window.alert(t('contact_fail'));
    });
  };

  return (
    <section className="contact" id="contact">
      <h2>{t('contact_us')}</h2>
      <div className="contact-info">
        <p><strong>{t('fci_sumerpur')}</strong></p>
        <p>{t('address_full')}</p>
        <p>{t('email')}: fcisumerpur@gmail.com</p>
        <p>{t('phone')}: 02933-255030, 9413592614, 9694994586, 9680675153</p>
      </div>
      <form className="contact-form" onSubmit={handleSubmit} noValidate>
        <input
          type="text"
          name="name"
          placeholder={t('your_name')}
          value={form.name}
          onChange={handleChange}
          required
        />
        {errors.name && <span className="error-msg">{errors.name}</span>}
        <input
          type="email"
          name="email"
          placeholder={t('your_email')}
          value={form.email}
          onChange={handleChange}
          required
        />
        {errors.email && <span className="error-msg">{errors.email}</span>}
        <textarea
          name="message"
          placeholder={t('your_message')}
          value={form.message}
          onChange={handleChange}
          required
        ></textarea>
        {errors.message && <span className="error-msg">{errors.message}</span>}
        <button type="submit">{t('send')}</button>
      </form>
    </section>
  );
}

export default Contact;
